mapper.select:
SELECT COUNT(*) > 0
FROM course_selections cs
INNER JOIN teaching_assignments ta ON cs.assignment_id = ta.assignment_id
WHERE cs.student_id = #{studentId}
AND ta.assignment_id = #{assignmentId}
AND cs.status = 'SELECTED'

SELECT COUNT(*) > 0
FROM course_selections cs
INNER JOIN teaching_assignments ta ON cs.assignment_id = ta.assignment_id
INNER JOIN courses c ON ta.course_id = c.course_id
WHERE cs.student_id = #{studentId}
AND cs.status = 'SELECTED'
AND ta.semester_id = #{semesterId}
AND c.course_day = #{courseDay}
AND c.course_time = #{courseTime}

SELECT COUNT(*) > 0
FROM course_selections cs
INNER JOIN teaching_assignments ta ON cs.assignment_id = ta.assignment_id
INNER JOIN courses c ON ta.course_id = c.course_id
WHERE cs.student_id = #{studentId}
AND cs.status = 'SELECTED'
AND ta.semester_id = #{semesterId}
AND c.classroom = #{classroom}
AND c.course_day = #{courseDay}
AND c.course_time = #{courseTime}

mapper.delete:
DELETE FROM course_selections
WHERE student_id = #{userId}
  AND assignment_id = #{courseId}
  AND status = 'selected'
